question.answer:
            #include <stdio.h>
int main (){
    char eingabe[256];
    int ausgabe = 0;
    int length = 0;

    scanf("%s", eingabe);
    
    while (eingabe[length] != '\0'){ /*Bestimmung der Länge des Strings*/
        length++;
    }
    
    
  
 for(int i = 0; i<length; i++){
     
    
    if(eingabe[i]=='V' || eingabe[i]=='v'){
        ausgabe = ausgabe + 5;
    }
    
    if(eingabe[i]=='L' || eingabe[i]=='l'){
        ausgabe = ausgabe + 50;
    }
    
    if(eingabe[i]=='D' || eingabe[i]=='d'){
        ausgabe = ausgabe + 500;
    } 
    
    if(eingabe[i]=='M' || eingabe[i]=='m'){
        ausgabe = ausgabe + 1000;
    } 
    
    if(eingabe[i] == 'I' || eingabe[i]=='i'){
        if(eingabe[i+1]== 'V' || eingabe[i+1]=='v'){ /* IV*/
            ausgabe = ausgabe + 4;
            i = i + 1;
            continue;
            
        }
        if(eingabe[i+1]== 'X' || eingabe[i+1]=='x'){ /* IX*/
            ausgabe = ausgabe + 9;
            i = i + 1;
            continue;
        }
        if(eingabe[i+1] !='V' && eingabe[i+1] !='X' && eingabe[i+1] != 'v' && eingabe[i+1] !='x'){
            ausgabe = ausgabe + 1;
        }
    }
    
    
    if(eingabe[i] == 'X' || eingabe[i]=='x'){
        if(eingabe[i+1]== 'L' || eingabe[i+1]=='l'){ /* XL*/
            ausgabe = ausgabe + 40;
            i = i + 1;
            continue;
        }
        if(eingabe[i+1]== 'C' || eingabe[i+1]=='c'){ /* XC*/
            ausgabe = ausgabe + 90;
            i = i + 1;
            continue;
        }
        if(eingabe[i+1] !='L' && eingabe[i+1] !='C' && eingabe[i+1] != 'l' && eingabe[i+1] !='c'){
            ausgabe = ausgabe + 10;
        }
    }
    

    
    if(eingabe[i] == 'C' || eingabe[i]=='c'){
        if(eingabe[i+1]== 'D' || eingabe[i+1]=='d'){ /* CD*/
            ausgabe = ausgabe + 400;
            i = i + 1;
            continue;
        }
        if(eingabe[i+1]== 'M' || eingabe[i+1]=='m'){ /* CM*/
            ausgabe = ausgabe + 900;
            i = i + 1;
            continue;
        } 
        if(eingabe[i+1] !='D' && eingabe[i+1] !='M' && eingabe[i+1] != 'd' && eingabe[i+1] !='m'){
            ausgabe = ausgabe + 100;
        }
    }
 }
    printf("%d", ausgabe);
    return 0;

}

