question.answer:
            #include <stdio.h>

void einlesen(int * u, float * v, int * w){
    
    printf("Gib einen ganzzahligen Wert fuer a ein: ");
        scanf("%d", u);
    printf("\nGib einen reellen Wert fuer x ein: ");
        scanf("%f", v);
    printf("\nGib einen ganzzahligen Wert fuer N ein: ");
        scanf("%d", w);
}

float reku (int p1, float p2, int p3){

    if(p3 !=0){
    
        return (p1+(p3*p2)) + reku(p1,p2,p3-1);
    }
    else{
        return (p1+(p3*p2));
    }
}

void ausgeben(float z){
    
    printf("\nDie Summe der Reihe ist: %.2f", z);
    
}

int main (void){
    
    int a,N;
    float x, summe_rek;
    
    einlesen(&a, &x, &N);
    summe_rek = reku(a, x, N);
    ausgeben(summe_rek);
    
    return 0;
}

