question.answer:
            #include <stdio.h>

int x = 5;

void f1 (int * u) {
    int x = 4;
    *u = 6;
    int t3 = 4;
    if(x == t3)
        printf ("\nf1 - der Wert von x ist %d", t3);
    t3 = x;
}

void f2 (int x) {
    int t2 = 7;
    if(x == t2)
        printf ("\nf2 - der Wert von x ist %d", t2);
}

int main (void) {
    int t1 = 5;
    int t4 = 6;
    
    if(x == t1)
        printf ("main - der Wert von x ist %d", t1);
    f2 (7);
    f1 (&x);
    if(x == t4)
        printf ("\nmain - der Wert von x ist %d", t4);
    return 0;
}
