question.answer:
void sort(int arrLen, int arr[]) {
    int tmp;
    int swapped = 0;
    do {
        swapped = 0;
        for(int i=0; i<arrLen-1; i++) {
            if(arr[i] > arr[i+1]) {
                swapped = 1;
                tmp = arr[i];
                arr[i] = arr[i+1];
                arr[i+1] = tmp;
            }
        }
    } while(swapped != 0);
}

