#include <stdio.h>

void sort(int arrLen, int arr[]) {
    int tmp;
    int swapped = 0;
    do {
        swapped = 0;
        for(int i=0; i<arrLen-1; i++) {
            if(arr[i] > arr[i+1]) {
                swapped = 1;
                tmp = arr[i];
                arr[i] = arr[i+1];
                arr[i+1] = tmp;
            }
        }
    } while(swapped != 0);
}

int main()
{

    int arr[10];
    for(int i=0; i<10; i++)
        scanf("%d",&arr[i]);

    sort(10,arr);

    for(int i=0; i<10; i++) 
        printf("%d\n",arr[i]);

    return 0;
}

