question.answer:
float Widerstand(float Ra, float Rb , float Rc ,int Aufbau)
{
    float Rg;
    switch(Aufbau){
        case 1 :
        Rg = Ra+Rb+Rc;

        break;

        case 2 :
        Rg = 1/((1/Ra)+(1/Rb)+(1/Rc));
        break;



        default:
        return 0;

    }   
    return Rg;
}
